import React from 'react'
import { View, StyleSheet } from 'react-native'
//J'importe ici tout les components que j'ai créer en amont.
import CardHeader from './CardHeader'
import CardFooter from './CardFooter'
import CardBody from './CardBody'

//Je déclare mon Component Fonction Card qui prend en paramètre {item} qui contient les données d'un item de ma flatlist
const Card = ({ item }) => {

    //Je desctructure mon tableau d'item pour récupérer chaque index de ce dernier.
    const {
        photo,
        brand,
        title,
        desc,
        price,
        size,
        likes
    } = item

    return (
        <View style={styles.container}>
            {/* Ici je passe a mon Component CardHeader le props "price" avec la valeur de price de mon item */}
            <CardHeader price={price} />
            {/* Ici je passe a mon Component CardBody les props 
            "photo" avec la valeur de photo de mon item
            "brand" avec la valeur de brand de mon item
            "title" avec la valeur de title de mon item
            "desc" avec la valeur de desc de mon item */}
            <CardBody photo={photo} brand={brand} title={title} desc={desc} />
            {/* Ici je passe a mon Component CardFooter les props 
            "size" avec la valeur de size de mon item
            "likes" avec la valeur de likes de mon item */}
            <CardFooter size={size} likes={likes} />
        </View>
    )
}
export default Card

const styles = StyleSheet.create({
    container: {
        flex: 1,
        borderWidth: 1,
        margin: 5,
        padding: 5,
        height: 350
    },
})
