import React from 'react'
import { StyleSheet, Text, View } from 'react-native'
import LikeBtn from './LikeBtn'

//Je crée mon Component Fonction qui prend en paramètre les props que j'ai passé depuis le component Parent "Card"
const CardFooter = (props) => {

    //Je desctructure mon tableau de props pour récupérer chaque index de ce dernier.
    const {
        size,
        likes
    } = props

    return (
        <View style={styles.container}>
    {/* Je me sert de ma constant size que je viens de récupérer grâçe a la destructuration plus haut et je le passe dans un text */}
                  <Text style={styles.size}>Taille : {size}</Text>
    {/* Je me sert de ma constant likes que je viens de récupérer grâçe a la destructuration plus haut et je le passe en props dans mon component LikeBtn */}
            <LikeBtn likes={likes} />
        </View>
    )
}
export default CardFooter

const styles = StyleSheet.create({
    container:{
        flexDirection: 'row',
        justifyContent: 'space-between',
        borderTopWidth: 1,
        height: 30,
    },
    size:{
        fontSize: 17,
        alignSelf: 'center'
    }
    
})
