import React from 'react'
import { Image, Text, View, StyleSheet, ScrollView } from 'react-native'

//Je crée mon Component Fonction qui prend en paramètre les props que j'ai passé depuis le component Parent "Card"
const CardBody = (props) => {

    //Je desctructure mon tableau de props pour récupérer chaque index de ce dernier.
    const {
        photo,
        brand,
        title,
        desc
    } = props

    return (
        <View style={styles.container}>
            {/* Je me sert de ma constant photo que je viens de récupérer grâçe a la destructuration plus haut et je le passe dans une image */}
            <Image source={photo} style={styles.image} />
             {/* Je me sert de ma constant brand et title que je viens de récupérer grâçe a la destructuration plus haut et je le passe dans un text */}
            <Text style={styles.title}>{brand} {title}</Text>
            <ScrollView>
                  {/* Je me sert de ma constant desc que je viens de récupérer grâçe a la destructuration plus haut et je le passe dans un text */}
                <Text numberOfLines={3}>{desc}</Text>
            </ScrollView>
        </View>
    )
}
export default CardBody

const styles = StyleSheet.create({
    container: {
        flex: 1
    },
    image: {
        marginVertical: 10,
        width: '100%',
        height: 150
    },
    title: {
        fontSize: 17,
        fontWeight: 'bold',
    }
})
