import React from 'react'
import {Text, View, StyleSheet} from 'react-native'

//Je crée mon Component Fonction qui prend en paramètre les props que j'ai passé depuis le component Parent "Card"
const CardHeader = (props) =>{

    //Je desctructure mon tableau de props pour récupérer chaque index de ce dernier.
    const {
        price
    } = props
    
    return(
        <View style={styles.container}>
        {/* Je me sert de ma constant price que je viens de récupérer grâçe a la destructuration plus haut et je le passe dans un text */}
        <Text style={styles.text}>{price}€</Text>
        </View>
    )
}
export default CardHeader

const styles = StyleSheet.create({
    container:{
        height: 30,
        borderBottomWidth: 1
    },
    text:{
        textAlign: 'right',
        fontSize: 20,
        fontWeight: 'bold',
        color: '#707070'
    }

})
