import React, { useEffect, useState } from 'react'
import { FlatList, LogBox, StatusBar, View } from 'react-native'
import Card from './src/components/Card'
import SearchBar from './src/components/SearchBar'
import PRODUITS from './src/constants/data'

export default function App() {
  const [search, setSearch] = useState([])

  const searchText = (text) => {
    var data = []
    PRODUITS.forEach((item) => {
      if (item.title.indexOf(text) != -1 || item.brand.indexOf(text) != -1) {
        data.push(item)
      }
    })  
    setSearch(data)
  }

  useEffect(() => {
    setSearch(PRODUITS)
  }, [])


  return (
    //Je créer ma flatlist en lui passant les paramètres suivants  :
    // -numColumns={2} pour afficher la liste sur deux colonnes.
    // -renderItem={Card} qui me permet d'utiliser mon component comme item de ma flatlist.
    // -data={PRODUITS} qui permet a ma Flatlist de savoir qu'il doit parcourir ma list de Produits.
    // -keyExtractor={PRODUITS.id} qui permet de créer un clé unique pour chacun de mes item.
    <View style={{ flex: 1 }}>
      <StatusBar />
      <SearchBar searchText={(text) => searchText(text)} />
      <FlatList
        numColumns={2}
        renderItem={Card}
        data={search}
        keyExtractor={search.id}
      />
    </View>
  );
}
